import './index.scss'

interface Props {
  wordsCount: number | undefined
  charactersCount: number | undefined
  sentencesCount: number | undefined
  paragraphsCount: number | undefined
}

const ResultBox = ({ wordsCount, charactersCount, sentencesCount, paragraphsCount }: Props) => {
  const resultBar = [
    {
      title: 'Words',
      value: wordsCount,
    },
    {
      title: 'Characters',
      value: charactersCount,
    },
    {
      title: 'Sentences',
      value: sentencesCount,
    },
    {
      title: 'Paragraphs ',
      value: paragraphsCount,
    },
    {
      title: 'Pronouns',
      value: 0,
    },
  ]

  return (
    <div className="result-bar">
      {resultBar.map(({ title, value }) => (
        <div className="result-box" key={title}>
          <span className="box-title">{title}</span>
          <span className="box-value">{value}</span>
        </div>
      ))}
    </div>
  )
}

export default ResultBox
