import './index.scss'

interface Props {
  wordsCount: number
}

const BottomResultBox = ({ wordsCount }: Props) => {
  const readingTime = (wordsCount: number) => {
    const time = wordsCount/225
    console.log(111, time)
    if (time > 0 && time < 1) {
      return 1
    }
    console.log(222, Math.ceil(time))
    return Math.floor(time)
  }
  const bottomResultBar = [
    {
      title: 'Average Reading Time:',
      value: '~' + readingTime(wordsCount) + ' ' + 'minute',
    },
    {
      title: 'Longest word:',
      value: '-',
    },
  ]

  return (
    <div className="bottom-result-bar">
      {bottomResultBar.map(({ title, value }) => (
        <div className="result-box" key={title}>
          <span className="box-title">{title}</span>
          <span className="box-value">{value}</span>
        </div>
      ))}
    </div>
  )
}

export default BottomResultBox
