import { useRef } from 'react'
import './index.scss'

interface Props {
  wordsCount: number | undefined
  setWordsCount: (value: number | undefined) => void
}

const TextArea = ({ wordsCount, setWordsCount }: Props) => {
  const textAreaRef = useRef<HTMLTextAreaElement>(null);

  const handleWordsCount = () => {
    const textAreaText = textAreaRef.current?.value
    const textCount = textAreaText?.split(/\s+/).length
    setWordsCount(textCount)
  }

  console.log(111, wordsCount)

  return <textarea className="text-area" placeholder="Paste your text here..." autoFocus onChange={handleWordsCount} ref={textAreaRef} />
}

export default TextArea
